INSERT INTO `o_reper` (`id`, `name`, `text`, `cat`, `play`, `f`, `author`, `group`, `date`, `minus`, `gpro`, `accord`, `vyb`, `preamb`) VALUES
(198, 'Englishman in New York', 'I don''t drink coffee I take tea my dear \r\nI like my toast done on one side \r\nAnd you can hear it in my accent when I talk \r\nI''m an Englishman in New York \r\n\r\nSee me walking down Fifth Avenue \r\nA walking cane here at my side \r\nI take it everywhere I walk \r\nI''m an Englishman in New York   \r\n\r\nRef\r\nI''m an alien \r\nI''m a legal alien \r\nI''m an Englishman in New York ...\r\n\r\nIf "manners maketh man" as someone said \r\nThen he''s the hero of the day \r\nIt takes a man to suffer ignorance and smile \r\nBe yourself no matter what they say \r\n\r\nRef\r\n\r\nModesty, propriety can lead to notoriety \r\nYou could end up as the only one \r\nGentleness, sobriety are rare \r\nin this society \r\nAt night a candle''s brighter than the sun \r\n\r\nTakes more than combat gear to make a man \r\nTakes more than license for a gun \r\nConfront your enemies, avoid them when you can \r\nA gentleman will walk but never run \r\n\r\nIf "manners maketh man" as someone said \r\nThen he''s the hero of the day \r\nIt takes a man to suffer ignorance and smile \r\nBe yourself no matter what they say \r\n\r\nRef', 0, 'uploads/sting---englishmen-in-new-york.mp3', 0, 'Sting', '', '', '', '', 'Bm  Bm7\r\nEm  A    Bm    Bm7   Modesty D                       A         A7\r\nBm           Bm7             F#\r\n G                        A\r\n  Gdim7                              Bm', 6, ''),
(199, 'Со мною вот что происходит', '\r\n\r\n        F#m          C#7/G#\r\nСо мною вот что происходит:\r\n           F#7/A#         Hm     Hm/F#\r\nКо мне мой старый друг не ходит,\r\n  Hm      E7/G#   A   D\r\nА ходят в праздной суете\r\nHm    Hm/A    C#\r\nРазнообразные не те.\r\n\r\n     Gm\r\n   И он не с теми ходит где-то,\r\n C7      Fmaj\r\n   И тоже понимает это,\r\n     Gm7   Gm6 Gm7     Gm6\r\n   И наш раздор необъясним,\r\n      Em75- A7       Dm   C#7\r\n   Мы оба мучаемся с ним.\r\n\r\n        F#m          C#7/G#\r\nСо мною вот что происходит:\r\n          F#7/A#       Hm     Hm/F#\r\nСовсем не та ко мне приходит,\r\nHm  E7/G# A          D\r\nМне руки на плечи кладёт\r\nHm     Hm/A        C#\r\nИ у другой меня крадёт.\r\n\r\n     Gm\r\n   А той, скажите, Бога ради,\r\n C7                           Fmaj\r\n   Кому на плечи руки класть?\r\n   Gm7     Gm6  Gm7  Gm6\r\n   Та, у которой я украден,\r\n       Em75-              A7      C#7\r\n   В отместку тоже станет красть.\r\n\r\n   F#m\r\nНе сразу этим же ответит,\r\nD\r\nА будет жить с собой в борьбе,\r\nG\r\nИ неосознанно наметит\r\nC#\r\nКого-то дальнего себе.\r\n\r\n   F#m     Hm7    E    Eadd9  A\r\nО, сколько нервных и недужных связей,\r\nC#m7    F#7   Hm        E       A\r\nДружб ненужных, во мне уже осатанённость,\r\nHm7           E   Eadd9 A\r\nО, кто-нибудь, приди, нарушь\r\nD          C#m7  F#7\r\nЧужих людей соединенность\r\nHm7 Eadd9 A     Hm7     C#\r\nИ разобщенность близких душ.\r\n\r\n        F#m          C#7/G#\r\nСо мною вот что происходит:\r\n           F#7/A#         Hm     Hm/F#\r\nКо мне мой старый друг не ходит,\r\n  Hm      E7/G#   A   D\r\nА ходят в праздной суете,\r\nHm     Hm/A   C#\r\nРазнообразные не те.\r\n\r\n        F#m          C#7\r\nСо мною вот что происходит,\r\n        F#m          C#7\r\nСо мною вот что происходит,\r\n        F#m          C#7\r\nСо мною вот что происходит...', 0, 'repert/112013/so_mnoiu_vot.mp3', 0, 'муз. Таривердиев М. / сл. Евтушенко Е.', '', '', '', '', 'Вступление: F#m | F#m \r\n F#m          C#7/G#\r\n F#7/A#         Hm     Hm/F#\r\n Hm      E7/G#   A   D\r\nHm    Hm/A    C#  \r\n  Gm    C7      Fmaj   Gm7   Gm6 Gm7     Gm6  Em75- A7       Dm   C#7', 6, ''),
(197, 'Hello, Dolly', 'Hello Dolly,......well, hello, Dolly\r\nIt''s so nice to have you back where you belong\r\nYou''re lookin'' swell, Dolly.......I can tell, Dolly\r\nYou''re still glowin''...you''re still crowin''...you''re still goin'' strong\r\nI feel that room swayin''......while the band''s playin''\r\nOne of your old favourite songs from way back when\r\nSo..... take her wrap, fellas.......find her an empty lap, fellas\r\n\r\nDolly''ll never go away again\r\n(instrumental break)\r\n\r\nHello Dolly,.....well, hello, Dolly\r\nIt''s so nice to have you back where you belong\r\nYou''re lookin'' swell, Dolly.....I can tell, Dolly\r\nYou''re still glowin''...you''re still crowin''...you''re still goin'' strong\r\nI feel the room swayin''...while that ole band keeps on playin''\r\nOne of your old favourite songs from way back when\r\nSo...golly, gee, fellas....find her an empty knee, fellas\r\nDolly''ll never go away....I said she''ll never go away\r\nDolly''ll never go away again\r\n', 0, 'repert/112013/Hello_Dolly.mp3', 0, 'Louis Armstrong ', '', '', '', '', 'C7 C E7 Fm7 G7 Em Am Cdim Cm7 D7 Gm7 Dm Bb ', 6, ''),
(196, 'Сенсация', ' Я сел однажды в медный таз\r\nБез вёсел и pуля,\r\nИ пеpеплыть Па-де-Кале\r\nНа нём решился я.\r\nВедь на подобном коpабле\r\nЧеpез пpолив Па-де-Кале\r\nНикто не плавал до меня.\r\n\r\nЯ вмиг озяб, я вмиг пpомок,\r\nПpопал весь мой поpыв...\r\nПpости мне, Господи, мой заскок,\r\nНо пусть я останусь жив!\r\nТо таз на мне, то я на нём,\r\nУж я не помню, кто на ком,\r\nНо пеpеплыли мы пpолив.\r\n\r\nИ вот — сенсация! На стенку лезет пpесса:\r\n— Впеpвые в миpе! Геpой пpогpесса!\r\nБез вёсел и pуля!\r\nПpедставьте себе — он плыл в тазу,\r\nПpи этом — ни в одном глазу!\r\nСенсация! И в центpе — я!\r\n\r\nЯ тут же пpодал медный таз\r\nЗа тысячу монет\r\nИ пеpепpодал свой pассказ\r\nВ тысячу газет.\r\nЕсть дом в кpедит и в банке счёт,\r\nКpугом почёт — чего ещё?\r\nНа всех консеpвах мой поpтpет!\r\n\r\nМой медный таз попал и в ТАСС,\r\nНа самый последний лист,\r\nПод рубрикою "Их мораль"\r\nИ подпись: "Журналист".\r\nНо, несмотря на злой поклёп,\r\nВ Москве я пожил, как набоб,\r\nПод вывескою "Интурист"!\r\n\r\nМужчины пpосят только одно — виски "Медный таз",\r\nВсе дамы носят только одно — клипсы "Медный таз",\r\nВесь миp танцует только одно — танго "Медный таз"\r\nПод самый модный медный джаз!\r\n\r\nНо вpемя шло, и шум иссяк,\r\nИ в банке счёт — увы!\r\nСемья бpанится так и сяк,\r\nИ нет уж той любви.\r\nДpузья пьют виски с содовой\r\nИ требуют: — Ещё давай!\r\nЕщё на чём-нибудь плыви!\r\n\r\nУж я не знаю, как мне быть,\r\nУ всех одно в башке:\r\n— В тазу тепеpь не модно плыть —\r\nВот если б в дуpшлаке!\r\nХотя игpа не стоит свеч:\r\nДуpшлак ведь может и потечь.\r\nПопpобуй на ночном гоpшке!\r\n\r\nИ вот сенсация, на стенку лезет пpесса:\r\n— Впеpвые в миpе! Геpой пpогpесса!\r\nДавайте сюда кино!\r\nИ я плыву как идиот,\r\nИ подо мной гоpшок плывёт,\r\nИ мы вот-вот пойдем на дно...\r\nИ мы вот-вот пойдем на дно...\r\nИ мы вот-вот... пошли\r\n', 0, 'uploads/uchene---svet.mp3', 0, 'Юлий Ким', '', '', '', '', 'E A H7', 6, ''),
(195, 'От большого ума', '\nОт большого ума - лишь сума да тюрьма.\nОт лихой головы - лишь канавы и рвы.\nОт красивой души только струпья и вши.\nОт вселенской любви только морды в крови.\n\nВ простыне на ветру, по росе поутру.\nОт бесплодных идей до бесплотных гостей,\nОт накрытых столов до пробитых голов,\nОт закрытых дверей до зарытых зверей.\n\nПараллельно пути черный спутник летит.\nОн утешит, спасет, он нам покой принесёт.\nПод шершавым крылом ночь за круглым столом.\nКрасно-белый плакат - "Эх, заводи самокат!"\n\nСобирайся, народ, на бессмысленный сход,\nНа всемирный совет - как обставить нам наш бред?\nВклинить волю свою в идиотском краю,\nПосидеть, помолчать да по столу постучать.\nВедь от большого ума лишь сума да тюрьма,\nОт лихой головы лишь канавы и рвы...\n', 0, 'repert/121-130/ot_bolshogo_uma.mp3', 0, 'Янка Дягилева', '', '', '', '', 'Cm B Fm G  ', 0, ''),
(200, 'Ты меня любишь', 'Дом обезлюдел, и я молюсь неистово,\nЗнаю, что любишь ты меня, неказистого.\nЧай пью тревожно, медленно чебуреки ем,\nА душу гложет Моцарта чудный “Реквием”.\n\nТы меня любишь, я тебя тоже люблю.\nОго. Пусть нескладно, зато по смыслу.\nЯ - страшно умный, есть во мне божья искра.\n“О” - это буква, ноль - это цифра. \n\nВыйду на поле в мятых трусах коричневых,\nЗнаю в футболе пару финтов гарринчевых,\nНа деревяшке выжгу тебя паяльников,\nСкину рубашку, спрячусь под пододеяльником.\nТы меня хочешь, Стало быть, перехочешь,О-о! \nТак случилось - Все меня хочут.\n\nБродишь не глядя ты по аллее буковой,\nСпрячусь в засаде, с ловкостью Чингачкуковой.\nВыскочу голый - “Здравствуй, моя отрада!”\nОго. Очень больно. Больше не надо.\n\n I love you, baby.I just believe in what you say.\nYes. It is table. And it was table yesterday.\nI love you, baby. Baby, you love my “имидж”.\n O-o. I am crazy. I can speak English.\n O-o. I am crazy. I can speak English.\n', 0, 'repert/30/Ty_menia_lubish.MP3', 0, 'Команда КВН НГУ-1989', '', '', '', '', 'Cm Fm G7 Cm Припев Cm          Fm           A#   G7\nCm     Fm       G7               Cm    ', 6, ''),
(192, 'La donna e mobile (from Rigoletto)', 'La donna e mobile\r\nQual piuma al vento\r\nMuta d''accento\r\nE di pensiero\r\n    \r\nSempre un amabile\r\nLeggiadro viso\r\nIn pianto o in riso\r\nE mensognero\r\n    \r\nLa donna e mobile\r\nQual piuma al vento\r\nMuta d''accento\r\nE di pensier\r\nE di pensier\r\nE di pensier\r\nE sempre misero\r\n    \r\nChi a lei s''affida\r\nChi le confida\r\nMal cauto il cor\r\nPur mai no sentesi\r\nFelice appieno\r\n\r\nChi sul quel seno\r\nNon liba amor\r\nLa donna e mobile\r\nQual piuma al vento\r\nMuta d''accento\r\nE di pensier\r\nE di pensier\r\nE di pensier', 0, 'repert/27/La_Donna_E_Mobile.mp3', 0, 'Verdi ', '', '', '', '', ' A D ... Fm E', 0, ''),
(191, 'Нет, ничто не ново под Луной', 'Нет, ничто не ново под Луной\r\nНовый век сменяет старый век\r\nСолнце поднимается с зарёю…\r\nПашет землю добрый человек\r\n\r\nДоброму для счастья надо ль много\r\nЛомоть хлеба да глоток воды\r\nИ стремясь душою ближе к богу,\r\nНе страшиться горя и беды\r\n\r\nНет, ничто не ново под Луной\r\nНовый век сменяет старый век\r\nБыл и остаётся сам собою\r\nЗавсегда хороший человек\r\n\r\nЭто просто быть таким хорошим\r\nНе юлить хвостом, не лаять вслед\r\nДа и быть свиньёю человек не может,\r\nЕсли он хороший человек…\r\n\r\nНет, что не ново под Луной\r\nНовый век сменяет старый век\r\nНо поёт, любуясь красотой земною\r\nНастоящий мудрый человек\r\n\r\nЧто нас ожидает – неизвестно\r\nНо старайся шанс не упустить\r\nБудь хорошим, добрым, мудрым, трезвым\r\nНе теряй серебренную нить\r\nНе теряй серебренную нить\r\n', 0, 'repert/30/nichto_ne_novo.wav', 0, 'Михаил Елецкий', '', '', '', '', 'C G Am F C G C G Припев Am Dm Em Am…', 0, ''),
(193, 'Квирин Кульман', 'Патриарх спрятал тетрадь и, проведя \nпальчиками по глазам:\n— Начнем великое дело с малого... \nПри Софье Алексеевне по моей слезной просьбе \nсхвачен на Кукуе \nпакостный еретик Квирин Кульман... \nНа допросе сказал: «Явился-де ему \nв Амстердаме некто в белых ризах \nи велел итти в Москву, там-де \nпогибают в мраке безверия... \n(Иоаким несколько помолчал от волнения.) \nИ вы, — говорил он на допросе, — слепы: \nне видите, — \nмоя голова в сиянии и устами \nговорит святой дух...» И приводил \nтексты из прелестных учений \nЯкова Бема и Христофора Бар-тута...\nА сам, между прочим, соблазнил на Москве \nдевку Марью Селифонтову, одел ее, — страха ради, \n— в мужское \nплатье, и живет она у него \nв чулане... По вся дни оба пьяны, \nна скрипке и тарелках играют, он высовывается \nв окошко и кричит бешеным голосом, что на него \nнакатил святой дух... \nИ пришедшим к нему пророчит и велит \nцеловать себя в низ живота... \nГосподи, как минуту спокойным быть, \nкогда здесь уже сатана ликует!.. \nПрошу великих государей указом вершить \nКвирина Кульмана, — сжечь его живым с книгами...\n', 0, '', 0, 'Алексей Толстой ("Пётр Первый")', '', '', '', '', '', 0, ''),
(189, 'День рожденья ', 'День рождения твой не на праздник похож \n- третье ноября\nИз гостей только я и докучливый дождь - \nвпрочем как вчера                        \nВ жизни как обычно не гармонии  \nДаже в доме твоём, где так любят тепло,\nхолодно душе                                    \nВот звучит в пустоте ему не назло\n песня о Мишель                                 \nВ жизни как обычно не гармонии  \nВ музыке только гармония есть\nВ музыке только гармония есть \nСлушай музыку и прощай..\nГоворят обошёл ты полсвета пешком \nв поисках любви                                   \nПочему люди верят всему так легко - \nвпрочем как и мы \nВ жизни как обычно не гармонии\nДень рождения твой не на праздник похож - \nтретье ноября \nВ этот день никого ты в свой дом не зовёшь -  \nвпрочем как и я\n\nВ жизни как обычно не гармонии  \nВ музыке только гармония есть \nВ музыке только гармония есть\nСлушай музыку и прощай.. \n', 0, 'repert/181-190/sench_den_rozdenia.mp3', 0, '\nАвтор текста Пушкина М. \nКомпозитор Максимов А.\n  ', '', '', '', '', 'Em Am H7 Em D G C G   ... Am Hm     Am H7 Em                                                      ', 0, ''),
(188, 'Happy Nation', 'Laudate omnes gentes laudate\r\nMagnificat in secula\r\nEt anima mea laudate\r\nMagnificat in secula\r\n\r\nHappy nation living in a happy nation\r\nWhere the people understand\r\nAnd dream of the perfect man\r\nA situation leading to sweet salvation\r\nfor the people for the good\r\nfor mankind brotherhood\r\n\r\nWe''re travelling in time\r\nIdeas by man are only that will last\r\nAnd over time we''ve learned from the past\r\nThat no man''s fit to rule the world alone\r\nA man will die but not his ideas\r\nHappy nation...\r\nWe''re travelling in time\r\nTravelling in time\r\nTell them we''ve gone too far\r\nTell them we''ve gone too far\r\nHappy nation come through\r\nAnd I will dance with you\r\nHappy nation\r\nHappy nation...', 0, 'repert/181-190/Happy_Nation.mp3', 0, '', 'Ace Of Base', '', '', '', 'Dm  Gm  A', 0, ''),
(187, 'Корабли врезают штевнем воду ', 'Корабли взрезают штевнем воду \r\nВоду океанов и морей\r\nЯ на мостике в любую непогоду,\r\nДаже в шторм я думаю о ней\r\n\r\nКорабли уходят от причала,\r\nИзчезая в дымке голубой\r\nПровожая, ты мне прокричала – \r\nГолос заглушил морской прибой\r\n\r\nТрудная работа – не простая\r\nВахты, штормы, месяцы в пути\r\nМы ещё с тобою наверстаем\r\nТо, что не успели обрести\r\n\r\n2 куплет\r\nДень и ночь стою я у штурвала\r\nМысленно я нашей встречи жду\r\nТы меня на пристани встречала,\r\nВерила, что я не пропаду\r\n\r\nВетер шквальный волны бьёт о скалы,\r\nКорабли бросает щепкой вниз\r\nТам в пучине нас любовь спасала, \r\nКогда на волоске была вся жизнь\r\n\r\nКорабли уходят от причала, \r\nИзчезая в дымке голубой\r\nТы на берегу меня встречала\r\nИ махала радостно рукой\r\n', 0, '', 0, 'Ирина Бердышева', '', '', '', '', 'Em Am C H7 E Am C H7 ... Edim7 H7 ', 7, ''),
(186, 'Сон', 'Всю песню играется рифф: \nE|-----------------------------|\nB|---5-------5-------5-------5-|\nG|---5-------5-------5-------5-|\nD|---4-------4-------4-------4-|\nA|-----------------------------|\nE|-5---0-0-5---0-0-5---0-0-5---|\n\nЯ смотрю один и тот же сон \nТакого не видел и Люк Бессон \nЕсли бы закрыл глаза. \nНебо как помятая фольга \nЯрко-красные снега \nВсе цвета здесь только ЗА! \n  \nНа месте солнца пятиконечная звезда \nВсе боятся опоздать \nПесню бы допеть успеть. \n  \nВсех строят строем \nСтрадают геморроем роем \nМА-МАгилы роют, доют \nЧтобы ближе быть к земле.(е-е) \n  \nWell ...\n  \nПерекрашены параши \nВ городе наши \nЛизергиновые каши \nПлачут наши на параде \nБога ради \nВыключайте радиоактивные \nМы можем \nДаже дышать ** порами кожи \nЕсли затянуть петлю потуже \nИли же заклеить рты \n  \nПеревернутое заперто \n  \nНаши пра-пра-предки \nОставили свои метки \nМедные монетки \nНаглядные пособия \nСанта-КЛАУСтрофобия \nРожденные в клетке \nКровоточит троеточие \n\nС утра до ночи я рублю дрова \nWow! Под топор попала голова \n  \nGame o-ver! \nGame o-ver! \n  \nWell ...\n\nДавно ли ли вы были на лесоповале? \nСтволы пылили, руки омывали \nРеки крови \nДа усы, да брови густые \nКопыта, хвосты (yeah) \n  \nПадали кресты, костры горели \nХором мы пели \nНо на самом деле - спали \nНас палили, чтобы \nВсе выше и выше и выше \nБыло пламя \nКрасное знамя над нами \nКрасное знамяяяя ! \n\n', 0, 'repert/181-190/5nizza_son.mp3', 0, '', '5nizza', '', '', '', 'Am               E  Am  E\nИли \nAm               E   E  Am\n', 0, ''),
(185, 'Романс генерала Чарноты (Булгаков, "Бег")', 'Опять один в постели полусонной,\r\nВо тьме ночной лишь стук шальных копыт.\r\nДавно лежит на золотых погонах\r\nПарижских улиц вековая пыль.\r\n\r\nБлестящие тускнеют офицеры,\r\nКак говорится, Боже, даждь нам днесь.\r\nУже не так изысканны манеры -\r\nОстались только выправка и честь.\r\n\r\nЯ жив, мой друг, покоен и свободен,\r\nНо стал мне часто сниться странный сон:\r\nНа водопой по василькам уводит\r\nСедой денщик коня за горизонт.\r\n     \r\nОсенним утром псовая охота,\r\nБорзые стелют, доезжачих крик.\r\nГустой туман спустился на болота,\r\nГде ждут своих тетерок глухари.\r\n \r\nКто мы с тобою здесь, на самом деле?\r\nОдин вопрос и лишь один ответ:\r\nMon cher ami, мы здесь с тобой Мишели,\r\nЗдесь нет Отечества и отчеств тоже нет.\r\n\r\nНе привыкать до первой крови драться,\r\nКогда пробьют в последний раз часы...\r\nНо, господа, как хочется стреляться\r\nСреди березок средней полосы.\r\n', 0, 'repert/181-190/opiat_odin_v_posteli.mp3', 0, 'Александр Розенбаум', '', '', '', '', '', 0, ''),
(184, 'I don''t know how to love him (Mary Magdalene)', '    I don''t know how to love him\r\n    What to do how to move him\r\n    I''ve been changed yes really changed\r\n    In these past few days when \r\n    I''ve seen myself\r\n    I seem like someone else\r\n\r\n    I don''t know how to take this\r\n    I don''t see why he moves me\r\n    He''s a man he''s just a man\r\n    And I''ve had so many men before\r\n    In very many ways\r\n    He''s just one more\r\n\r\n    Should I bring him down should \r\n     I scream and shout\r\n    Should I speak of love let my feelings out\r\n    I never thought I''d come to this what''s it all about\r\n\r\n    Don''t you think it''s rather funny\r\n    I should be in this position\r\n    I''m the one who''s always been\r\n    So calm so cool, no lover''s fool\r\n    Running every show\r\n    He scares me so\r\n\r\n    I never thought I''d come to this what''s it all about\r\n    Yet if he said he loved me\r\n    I''d be lost I''d be frightened\r\n    I couldn''t cope just couldn''t cope\r\n    I''d turn my head I''d back away\r\n    I wouldn''t want to know\r\n    He scares me so\r\n    I want him so\r\n    I love him so\r\n', 0, 'repert/181-190/I_Don_Know_How_To_Love_Him.mp3', 0, 'Эндрю Ллойд Уэббер и Тим Райс', '', '', '', '', 'D G D G .. Em A Hm F# ... C D G ', 0, ''),
(183, 'Товарищи учёные', 'Товарищи ученые, доценты с кандидатами.\r\nЗамучились вы с иксами, запутались в нулях.\r\nСидите, разлагаете молекулы на атомы,\r\nЗабыв, что разлагается картофель на полях.\r\n\r\n   Из гнили да из плесени бальзам извлечь пытаетесь\r\n   И корни извлекаете по десять раз на дню.\r\n   Ох, вы там добалуетесь. Ох, вы доизвлекаетесь,\r\n   Пока сгниет, заплеснеет картофель на корню.\r\n\r\nАвтобусом до Сходни доезжаем,\r\nА там - рысцой. И не стонать!\r\nНебось картошку все мы уважаем,\r\nКогда с сальцой ее помять.\r\n\r\n   Вы можете прославиться почти на всю Европу, коль\r\n   С лопатами проявите здесь свой патриотизм.\r\n   А то вы всем кагалом там набросились на опухоль,\r\n   Собак ножами режете, а это - бандитизм.\r\n\r\nТоварищи ученые, кончайте поножовщину.\r\nБросайте ваши опыты, гидрид и ангидрид.\r\nСадитесь на автобусы, валяйте к нам в Тамбовщину,\r\nА гамма-излучение денек повременит.\r\n\r\n   Автобусом к Тамбову подъезжаем,\r\n   А там - рысцой. И не стонать!\r\n   Небось картошку все мы уважаем,\r\n   Когда с сальцой ее помять.\r\n\r\nК нам можно даже с семьями, с друзьями и знакомыми.\r\nВсе ловко здесь разместимся, сами скажете потом,\r\nЧто бог мол с ними с генами, бог с ними с хромосомами\r\nМы славно поработали и славно отдохнем.\r\n\r\n   Товарищи ученые, Эйнштейны драгоценные,\r\n   Ньютоны ненаглядные, любимые до слез.\r\n   Ведь лягут в землю общую остатки наши бренные,\r\n   Земле ведь все едино - апатиты и навоз.\r\n\r\nНакроем стол скатеркою. Валяйте, ешьте пальцами.\r\nХоть вы там создаете синтетический белок.\r\nНо он такой невкусный. Мы ж вас накормим яйцами,\r\nДадим с собой картофеля, хоть сумку, хоть мешок.\r\n\r\n   Для вас тот день покажется и каторжный и адовый,\r\n   Сырой картофель в грядках у ученых не в чести.\r\n   Зато впервые сможете повкалывать наглядно вы\r\n   И пользу ощутимую народу принести.\r\n\r\nТак приезжайте, милые, рядами и колоннами.\r\nХотя вы все там химики и нет на вас креста,\r\nНо вы ж там все задохнетесь, за синхрофазотронами,\r\nА здесь места отличные, воздушные места.\r\n\r\n   Товарищи ученые. Не сумневайтесь, милые,\r\n   Коль, что у вас не ладится, ну там не тот эффект,\r\n   Мы мигом к вам заявимся с лопатами и с вилами,\r\n   Денечек покумекаем и выправим дефект.\r\n', 0, '', 0, 'Владимир Высоцкий', '', '', '', '', '', 0, ''),
(182, 'Money, Money, Money', 'I work all night, I work all day\r\nTo pay the bills I have to pay\r\nAin''t it sad?\r\nAnd still there never seems to be\r\nA single penny left for me\r\nThat''s too bad\r\nIn my dreams I have a plan\r\nIf I got me a wealthy man\r\nI wouldn''t have to work at all\r\nI''d fool around and have a ball\r\n\r\nMoney, money, money\r\nMust be funny\r\nIn the rich man''s world\r\nMoney, money, money\r\nAlways sunny\r\nIn the rich man''s world\r\nAll the things I could do\r\nIf I had a little money\r\nIt''s a rich man''s world\r\nIt''s a rich man''s world\r\n\r\nA man like that is hard to find\r\nBut I can''t get him off my mind\r\nAin''t it sad?\r\nAnd if he happens to be free\r\nI bet he wouldn''t fancy me\r\nThat''s too bad\r\nSo I must leave, I''ll have to go\r\nTo Las Vegas or Monaco\r\nAnd win a fortune in a game\r\nMy life will never be the same\r\n\r\nMoney, money, money\r\nMust be funny\r\nIn the rich man''s world\r\nMoney, money, money\r\nAlways sunny\r\nIn the rich man''s world\r\nAll the things I could do\r\nIf I had a little money\r\nIt''s a rich man''s world\r\n\r\nMoney, money, money\r\nMust be funny\r\nIn the rich man''s world\r\nMoney, money, money\r\nAlways sunny\r\nIn the rich man''s world\r\nAll the things I could do\r\nIf I had a little money\r\nIt''s a rich man''s world\r\nIt''s a rich man''s world \r\n', 0, 'repert/181-190/Money_Money_Money.Mp3', 0, 'B. Andersson, B. Ulveus', '', '', '', '', 'E', 0, ''),
(181, 'From Souvenirs To Souvenirs', 'A lonely room and empty chair \r\nAnother day so hard to bear \r\nThe things around me that \r\nI see remind me of \r\nThe past and how it all used to be \r\n\r\nFrom souvenirs to more souvenirs I live \r\nWith days gone by when our hearts had all to give \r\nFrom souvenirs to more souvenirs I live \r\nWith dreams you left behind \r\n\r\nI''ll keep on turning in my mind \r\nThere''ll never be another you \r\nNo one will share the worlds we knew \r\nAnd now that loneliness has come to take your place \r\nI close my eyes and see your face\r\n\r\nFrom souvenirs to more souvenirs I live \r\nWith days gone by when our hearts had all to give \r\nFrom souvenirs to more souvenirs I live \r\nWith dreams you left behind \r\nI''ll keep on turning in my mind...\r\n', 0, 'repert/181-190/From_Souvenirs_To_Souvenirs.mp3', 0, '', 'Demis Roussos', '', '', '', 'Cm F B D# G# G Cm ...', 0, ''),
(180, 'Песенка Крокодила Гены', 'Пусть бегут неуклюже пешеходы по лужам\r\nА вода по асфальту рекой\r\nИ не ясно прохожим в этот день непогожий\r\nПочему я веселый такой\r\n\r\nА я играю на гармошке у прохожих на виду\r\nК сожаленью день рожденья\r\nТолько раз в году\r\n\r\nПрилетит вдруг волшебник в голубом вертолете\r\nИ бесплатно покажет кино\r\nС днем рожденья поздравит\r\nИ наверно оставит мне в подарок пятьсот эскимо\r\n\r\nА я играю на гармошке у прохожих на виду\r\nК сожаленью день рожденья\r\nТолько раз в году\r\nК сожаленью день рожденья\r\nТолько раз в году', 0, 'repert/171-180/pust_begut.mp3', 0, 'Композитор: Шаинский В Автор слов: Тимофеевский А.', '', '', '', '', 'Am E ... D', 0, ''),
(179, 'Голубой вагон', ' Медленно минуты уплывают вдаль\r\n Встречи с ними ты уже не жди\r\n И хотя нам прошлого немного жаль\r\n Лучшее конечно впереди\r\n                          \r\nСкатертью скатертью дальний путь стелится\r\nИ упирается прямо в небосклон\r\nКаждому каждому в лучшее верится\r\nКатится катится голубой вагон\r\n\r\nМожет мы обидели кого-то зря\r\nКалендарь закроет этот лист\r\nК новым приключениям спешим друзья\r\nЭй прибавь-ка ходу машинист\r\n                          \r\nСкатертью скатертью дальний путь стелится\r\nИ упирается прямо в небосклон\r\nКаждому каждому в лучшее верится\r\nКатится катится голубой вагон\r\n\r\nГолубой вагон бежит качается\r\nСкорый поезд набирает ход\r\nНу зачем же этот день кончается\r\nПусть бы он тянулся целый год\r\n                          \r\nСкатертью скатертью дальний путь стелится\r\nИ упирается прямо в небосклон\r\nКаждому каждому в лучшее верится\r\nКатится катится голубой вагон\r\nКаждому каждому в лучшее верится\r\nКатится катится голубой вагон', 0, 'repert/171-180/goluboi_vagon.mp3', 0, 'Слова: Тимофеевский А.  Музыка  Шаинский В.', '', '', '', '', 'Em Am D G Hm ...', 0, ''),
(178, 'Песенка бандитов (из м/ф "Приключения капитана Врунгеля")', 'C\r\nМы бандито, гангстерито,\r\n                  E\r\nМы кастето, пистолето,\r\n\r\noh yes !\r\n        Gm\r\nМы стрелянто,\r\n                         A\r\nУбиванто, украданто то и это.\r\n\r\noh, yes.\r\n         A7          D\r\nБанко, тресто, президенто,\r\n      G          C\r\nОграблянто ун моменто.\r\n     Dm7\r\nИ за енто,\r\n     F            G         C\r\nРежисенто нас сниманто киноленто.\r\n\r\n\r\nМы бандито знаменито,\r\nМы стрелято пистолето.\r\noh, yes.\r\nМы фиато,\r\nРазьежато целый день кабриолето.\r\noh, yes.\r\nПостоянно пьем чинзано,\r\nПостоянно сыто-пьяно.\r\nДержим банко\r\nМиллионо и плеванто на законо\r\n', 0, 'repert/171-180/bandito-gans.mp3', 0, '', '', '', '', '', 'C E Gm A A7 D G C Dm7 F G C', 0, ''),
(177, 'Песня разбойников ', '       Am        E          Am\r\nПусть нету ни кола и не двора.\r\n          Dm    G7       C     A7\r\nЗато не платят королю налоги\r\n   Dm      E7         F\r\nРаботники ножа и топора -\r\n   Dm    E7             Am\r\nРомантики  с большой дороги.\r\n\r\n       E7        Am\r\n  Пр: Не желаем жить по другому,\r\n       G7        C        A7\r\n      Не желаем жить, эх по другому,\r\n       Dm    G7           C     A7\r\n      Ходим мы по окраю, ходим мы по краю,\r\n       Dm           E7     Am\r\n      Ходим мы по краю родному.\r\n\r\nМы бродим от утра и до утра.\r\nЧужие сапоги натерли ноги.\r\nРаботникам ножа и топора -\r\nРомантикам с большой дороги.\r\n\r\nПрохожих ищем с ночи до утра.\r\nНу от чего не любят недотроги\r\nРаботников ножа и топора -\r\nРомантиков с большой дороги.\r\n', 0, 'repert/171-180/pesnia_razboinikov.Mp3', 0, '', '', '', '', '', 'Am E Am Dm G7 C A7 Dm E7 F Dm E7 Am Припев H7 Am G7 C A7 Dm G7 C A7 Dm E7 Am', 0, ''),
(176, 'Луч солнца золотого', 'Луч солнца золотого тьмы скрыла пелена\r\n И между нами снова вдруг выросла стена\r\n\r\n ПРИПЕВ:\r\n F     G\r\n А-А-А а-а-а..\r\n  C                     F    G\r\n Ночь пройдет, наступит утро ясное\r\n   C                 F      G\r\n Знаю счастье нас с тобою ждет\r\n  C                    F       G\r\n Ночь пройдет, пройдет пора ненастная\r\n    Am             F  G  C             F  G\r\n Солнце взойдет,      солнце взойдет\r\n\r\nПеть птицы перестали\r\nСвет звезд коснулся крыш\r\nВ час грусти и печали\r\nТы голос мой услышь\r\n', 0, 'repert/171-180/luch_solnca_zolotogo.mp3', 0, '', '', '', '', '', 'C G Am Em ... C F G C Am G ...', 0, ''),
(175, 'It''s a different world', 'Verse\r\nDifferent kind of people, \r\ntelling me that snow was white.\r\nIn former days the grass was green, \r\nthe sun was really bright.\r\nYou had to fight for freedom, that''s okay.\r\n\r\nChorus\r\nIt''s a different world,\r\nit''s a different world,\r\nit''s a different world today.\r\n\r\nVerse\r\nNowadays they drive a car \r\nat eighty miles an hour.\r\nYears ago there wasn''t all \r\nthat traffic, so much power.\r\nThere must be some improvement anyway.\r\n\r\nBridge\r\nDon''t look back,\r\nthe world has got a lot in store.\r\nWait and see,\r\npeople always wanna have more, more, more.\r\nWith all the ups and downs along the way.\r\n\r\nVerse\r\nNow that I''m a woman, \r\nI see them children grow.\r\nAnd deep inside I wished, \r\nI was so little, but I know.\r\nYou can''t turn back the pages so they say.\r\n', 0, 'repert/171-180/Its_A_Different_World.mp3', 0, 'Music: Alice May, Words: Alice May', '', '', '', '', 'Am C F E ... Dm F E ... F G C', 0, ''),
